import {useState} from "react"


function App() {

  const [todos, setTodos]=useState(["t1","t2","t3"]);


  return (
    <div className="row justify-content-center mt-5">
      <div className="col-md-4" > 

         <div className="card">
           <div className="card-body text-bg-danger opacity-75">
              <h2>Todo App</h2>
              <p>Streamline Your Day, the React Way!</p>
              <hr/>
              <ul>
              {
                todos.map(todo=> {
                     return <div className="text-bg-light">{todo}</div>
                })
              }
              </ul>
               <input
               className="w-100 py-2"
               type="text"
                placeholder="enter a todo"
               />
            </div>

         </div>
      </div>

    </div>
  );
}

export default App;
