import {useState} from "react"
import "bootstrap/dist/css/bootstrap.min.css";
import {Card } from "react-bootstrap"

function App() {

  const [todos, setTodos]=useState([{id:1,title:"t1",completed:true},{id:2,title:"t2",completed:false}]);


  return (
    <div className="row justify-content-center mt-5">
      <div className="col-md-4" > 

         <div className="card">
           <div className="card-body text-bg-danger opacity-75">
              <h2>Todo App</h2>
              <p>Streamline Your Day, the React Way!</p>
              <hr/>
              {
                todos.map(todo=> {
                     return <div 
                     className="text-bg-secondary mb-1 py-3 w-100"
                     >{todo.title}

                     </div>
                })
              }
               <input
               className="w-100 py-2"
               type="text"
                placeholder="enter a todo"
               />
            </div>

         </div>
      </div>

    </div>
  );
}

export default App;
