import {useState} from "react"
import "bootstrap/dist/css/bootstrap.min.css";
import {Card, Row, Col } from "react-bootstrap"


function App() {
  const [todos, setTodos]=useState([{id:1,title:"t1",completed:true},{id:2,title:"t2",completed:false}]);

  return (
  <Row center>
      <Col sm>
       <Card> 
        <Card.Body>
          <Card.Title>
           Todo App
          </Card.Title>
          <Card.Text>
             Streamline Your Day, the React Way!
          </Card.Text>
          <hr/>
          { todos.map(todo=> {
                     return <div 
                     className="text-bg-secondary mb-1 py-3 w-100"
                     >{todo.title}                   
                     </div>
                })
          }
               <input
               className="w-100 py-2"
               type="text"
                placeholder="enter a todo"
               />
        </Card.Body>
       </Card>
       </Col>
    </Row>
  );
}

export default App;
